/*
Rudimentary test framework.

Based on this tutorial:
https://code.tutsplus.com/tutorials/javascript-testing-from-scratch--net-23927
*/

(function() {
    var equal = require('deep-equal');
    
    var resultOutput = function(msg, result, expected, actual) {
        var resultLabel = result ? 'PASS' : 'FAIL';
        
        console[result ? 'log' : 'warn']('Test: ' + msg + ' :: ' + resultLabel);  
        
        if (!result) {
            console.warn(
                '-- Expected: ' + expected + '\n-- Actual: ' + actual);
        }
    };
    
    var areEqual = function(expected, actual, msg) {
        var result = equal(expected, actual);
        
        resultOutput(msg, result, expected, actual);
        
        return result;
    };
    
    var areNotEqual = function(expected, actual, msg) {
        var result = !equal(expected, actual);
        
        resultOutput(msg, result, expected, actual);
        
        return result;
    };
    
    module.exports = {
        areEqual: areEqual,
        areNotEqual: areNotEqual
    };
})();