/*
Rudimentary test framework.

Based on this tutorial:
https://code.tutsplus.com/tutorials/javascript-testing-from-scratch--net-23927
*/

(function() {
    var resultOutput = function(msg, result) {
        var resultLabel = result ? 'PASS' : 'FAIL';
        
        console[result ? 'log' : 'warn']('Test: ' + msg + ' :: ' + resultLabel);  
    };
    
    var areEqual = function(a, b, msg) {
        var result = (a === b);
        
        resultOutput(msg, result);
        
        return result;
    };
    
    var areNotEqual = function(a, b, msg) {
        var result = (a !== b);
        
        resultOutput(msg, result);
        
        return result;
    };
    
    module.exports = {
        areEqual: areEqual,
        areNotEqual: areNotEqual
    };
})();